mapper.select:
SELECT SUMMONER_ID
FROM USER_INFO 
WHERE USER_ID = #{user_id}

SELECT SUMMONER_ID
FROM USER_INFO 
WHERE USER_PWD = #{user_pwd}

SELECT USER_NO, BAN, SUMMONER_ID, REPORT_STATUS, USER_PWD
FROM USER_INFO
WHERE USER_ID = #{user_id} AND USER_PWD = #{user_pwd}

SELECT USER_ID
FROM USER_INFO
WHERE USER_ID = #{user_id}

SELECT COUNT(*)
FROM USER_INFO
WHERE SUMMONER_ID = #{summoner_id}

SELECT SOLO_TIER
FROM LOL_INFO
WHERE USER_NO = #{user_no}

mapper.update:
UPDATE USER_INFO
SET REPORT_STATUS = REPORT_STATUS-1, BAN = 1
WHERE USER_NO = #{user_no}
